metric_query:
SELECT (SUM({var__rvu_column}) / SUM({var__days_column})) + STDDEV({var__rvu_column}/{var__days_column}) AS result
FROM {var__trp_table}
WHERE  {CORE_FILTERS} AND {TRP_FILTERS};

group_by_1_query:
SELECT g.vg1, s.dev  AS result
FROM 
( SELECT {var__group_by_1} AS vg1, 1 AS temp
FROM {var__trp_table}
WHERE {CORE_FILTERS} AND {TRP_FILTERS}
GROUP BY {var__group_by_1} 
) as g 
LEFT OUTER JOIN (
SELECT (SUM({var__rvu_column}) / SUM({var__days_column})) + STDDEV({var__rvu_column}/{var__days_column}) AS dev, 1 AS temp 
FROM {var__trp_table} 
WHERE {CORE_FILTERS} AND {TRP_FILTERS}
) AS s 
ON s.temp = g.temp;

group_by_2_query:
SELECT g.vg1 , g.vg2, s.dev AS result
FROM 
( SELECT {var__group_by_1} AS vg1, {var__group_by_2} AS vg2, 1 AS temp
FROM {var__trp_table}
WHERE {CORE_FILTERS} AND {TRP_FILTERS}
GROUP BY {var__group_by_1}, {var__group_by_2} 
) as g 
LEFT OUTER JOIN (
SELECT (SUM({var__rvu_column}) / SUM({var__days_column})) + STDDEV({var__rvu_column}/{var__days_column}) AS dev, 1 AS temp 
FROM {var__trp_table} 
WHERE {CORE_FILTERS} AND {TRP_FILTERS}
) AS s 
ON s.temp = g.temp;
