metric_query:
select {var__function}({var__field}) as result 
from {var__trp_table}  
where {CORE_FILTERS} and {TRP_FILTERS}

group_by_1_query:
select {var__group_by_1} as vg_1, {var__function}({var__field}) as result 
from {var__trp_table}  
where {CORE_FILTERS} and {TRP_FILTERS}
group by {var__group_by_1}
order by {var__order_by}

group_by_2_query:
select {var__group_by_2} as v_g2, {var__group_by_1} as v_g1, {var__function}({var__field}) as result
from {var__trp_table}  
where {CORE_FILTERS} and {TRP_FILTERS}
group by {var__group_by_2}, {var__group_by_1}
order by {var__order_by};
